import React, { useState } from 'react';
import Button from "@mui/material/Button";
import { TextInput, DropdownSelection, DateSelection, AlertMessage } from "../../../components";
import './ClientForm.css';
import { useClientStore } from '../../../store';
import { useCountries } from '../../../hooks';
import { Client } from '../';
import { format } from "date-fns";
import { useForm, Controller } from "react-hook-form";

interface InitialState {
  name?: string;
  surname?: string;
  country?: Date;
  birthday?: string;
}

const getInitialState = (): InitialState => ({
  name: undefined,
  surname: undefined,
  country: undefined,
  birthday: undefined,
});

type Props = {
  clients: Client[];
  setClients: (value: Client[]) => void;
};


const ClientForm: React.FC<Props> = ({ clients, setClients }) => {
  const defaultValues = getInitialState();
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors },
  } = useForm();
const [alert, setAlert] = useState<string>();
  const countries = useCountries();

  const {
    name,
    setName,
    surname,
    setSurname,
    country,
    setCountry,
    birthday,
    setBirthday,
  } = useClientStore((state) => ({
    name: state.name,
    setName: state.setName,
    surname: state.surname,
    setSurname: state.setSurname,
    country: state.country,
    setCountry: state.setCountry,
    birthday: state.birthday,
    setBirthday: state.setBirthday
  }));

  const onSubmit = (data: any) => console.log(data);

  console.log(watch("example")); // watch input value by passing the name of it

  const handleOnSave = () => {
    if(!birthday) return;

    const newClient = {
      name,
      surname,
      country,
      birthday,
    };

    const birthdayDate = new Date(birthday);
    const yearsOld = parseInt(format(new Date(), "yyyy")) - parseInt(format(birthdayDate, "yyyy"));

     const newAlert = `Hello ${name} from ${country} on ${format(
       birthdayDate,
       "dd"
     )} of ${format(birthdayDate, "MM")} you will have ${yearsOld}}`;

    setAlert(newAlert);

    setClients([...clients, newClient]);

  };


      return (
        <form onSubmit={handleSubmit(onSubmit)}>
          <div className="client-form">
            <TextInput
              label={"Name"}
              defaultValue={"Name Here"}
              value={name}
              setValue={setName}
            />
            <TextInput
              label={"Surname"}
              defaultValue={"Name Here"}
              value={surname}
              setValue={setSurname}
            />
            {countries.length > 0 && (
              <DropdownSelection
                label={"Countries"}
                value={country}
                setValue={setCountry}
                data={countries}
              />
            )}
            <DateSelection
              label={"Birthday"}
              value={birthday}
              setValue={setBirthday}
            />
            <div className="client-form--submition">
              <Button variant="outlined" onClick={handleOnSave}>
                Save
              </Button>
            </div>
            <div className="client-form--alert">
              <AlertMessage message={alert} />
            </div>
          </div>
        </form>
      );
    };

export default ClientForm;
