import { useQuery } from "react-query";
import { getFlights } from "../api";

type HookParams = {
  offset: number;
  limit: number;
};

type Hook = (params: HookParams) => any;

const useFlights: Hook = (params) => {
  const { data, isFetching } = useQuery<any>("photos", () => getFlights(params), {
    refetchOnWindowFocus: false,
    staleTime: 60000,
  });

  if (isFetching) return { isLoading: isFetching, countries: [] };

  return {data};
};

export default useFlights;
