import { useQuery } from "react-query";
import { getAircrafts } from "../api";

type HookParams = {
  offset: number;
  limit: number;
};

type Hook = (params: HookParams) => any;

const useAircrafts: Hook = (params) => {
  const { data: aircrafts, isFetching } = useQuery<any>("photos", () => getAircrafts(params), {
    refetchOnWindowFocus: false,
    staleTime: 60000,
  });

  if (isFetching) return { isLoadingAircrafts: isFetching, aircrafts: [] };

  return { aircrafts: aircrafts.data };
};

export default useAircrafts;
