import create, { SetState } from "zustand";

type ClientForm = {
  name?: string;
  setName: (newName: string) => void;
  surname?: string;
  setSurname: (newSurname: string) => void;
  country?: string;
  setCountry: (newCountry: string) => void;
  birthday?: string;
  setBirthday: (newBirthday: string) => void;
};

const useClientStore = create<ClientForm>((set: SetState<ClientForm>) => ({
  name: undefined,
  setName: (value) => set({ name: value }),
  surname: undefined,
  setSurname: (value) => set({ surname: value }),
  country: undefined,
  setCountry: (value) => set({ country: value }),
  birthday: undefined,
  setBirthday: (value) => set({ birthday: value }),
}));

export default useClientStore;
