import React, { useState } from 'react';
import ArrowBackIosIcon from "@mui/icons-material/ArrowBackIos";
import ArrowForwardIosIcon from "@mui/icons-material/ArrowForwardIos";
import Grid from "@mui/material/Grid";
import { addDays, subDays, format } from 'date-fns';

const Header: React.FC = () => {
    const [date, setDate] = useState(new Date());

    const handleNextDay = () => {setDate(addDays(date, 1))};
    const handlePreviousDay = () => {setDate(subDays(date, 1))};

    const dayFormatted = format(date, "do LLLL y"); 


    return (
      <Grid key={"header"} item>
        <div style={{ display: "flex" }}>
          <ArrowBackIosIcon color="primary" onClick={handlePreviousDay} />
          <div style={{ paddingTop: "3px", paddingRight: "5px" }}>
            {dayFormatted}
          </div>
          <ArrowForwardIosIcon color="primary" onClick={handleNextDay} />
        </div>
      </Grid>
    );
};

export default Header;
