import { useQuery } from "react-query";

import { Photos } from "../api";

type Hook = (
  text: string
) => 
  { photos?: Photos, isLoading: boolean };

const usePhotos: Hook = (text) => {
     const { data, isFetching } = useQuery<any>(
      ["photos", text],
      () => {},
      {
        refetchOnWindowFocus: false,
        staleTime: 60000,
      }
    );

    if(isFetching) return { isLoading: isFetching};

    return { photos: data?.photos, isLoading: isFetching};
};

export default usePhotos;
