import React, { useState } from 'react';
import Stack from "@mui/material/Stack";
import { FlightItem } from './FlightItem';
import { Paper } from '@mui/material';
import "./Flights.css";
import { Flight } from '../types';
import ErrorIcon from "@mui/icons-material/Error";

type Props = {
    flights: any;
};

const Flights: React.FC<Props> = ({flights}) => {
  const [hasError, setHasError] = useState(false);

    return (
      <Paper sx={{ height: "90vh", minWidth: "211px" }}>
        <div>
          <div className="flights-title">
            <b>Flights</b>
          </div>
          <div>{hasError && <ErrorIcon sx={{ color: "#e63946" }} />}</div>
        </div>
        <Stack
          direction="column"
          justifyContent="space-evenly"
          alignItems="center"
          spacing={2}
        >
          <div className="flights-body">
            {flights.map((flight: Flight) => (
              <FlightItem flight={flight} key={`flightItem-${flight.id}`} hasError={hasError} setHasError={setHasError}/>
            ))}
          </div>
        </Stack>
      </Paper>
    );
};

export default Flights;
