import React, { useEffect, useState } from "react";
import "./MainPage.css";
import { ClientsTable } from "./ClientsTable";
import { ClientForm } from "./ClientForm";
import { Client } from './types';
import { Switch, Button, ButtonGroup } from '@mui/material';
import './MainPage.css'

type Props = {
  propClients: Client[];
  setStoreValues: (newClients: Client[]) => void;
};

const MainPage: React.FC<Props> = ({ propClients, setStoreValues }) => {
  const [clients, setClients] = useState<Client[]>(propClients);

  console.log('clients -> ', clients)

  useEffect(() => {
    if (clients.length === 0) return;

    setStoreValues(clients);
  }, [clients]);

  return (
    <>
    <div className="header">
      <ButtonGroup variant="contained" aria-label="outlined primary button group">
        <Button>One</Button>
        <Button>Two</Button>
        <Button>Three</Button>
      </ButtonGroup>
    </div>
    <div className="main-content">
      <ClientForm clients={clients} setClients={setClients} />
      <ClientsTable clients={clients} />
    </div>
    </>
  );
};

export default MainPage;
