import * as React from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import './TextInput.css'
import { ChangeEvent } from "react";
import { Controller, useFormContext } from "react-hook-form";

type Event = ChangeEvent<HTMLInputElement | HTMLTextAreaElement>;

type Props = {
  label: string;
  defaultValue: string;
  value: string;
  setValue: (newValue: string) => void;
};

const TextInput: React.FC<Props> = ({label, defaultValue, setValue}) => {
  const methods = useFormContext<any>();

  const { register, control } = methods;

  //const handleOnChange = (e: Event) => setValue(e.target.value);

  return (
    <Box
      component="form"
      sx={{
        "& .MuiTextField-root": {
          marginTop: "8px",
          marginBottom: "8px",
          marginLeft: "4px",
          marginRight: "4px",
          display: "grid",
        },
      }}
      noValidate
      autoComplete="off"
    >
      <Controller
        control={control}
        name="nameField"
        render={({ onChange, value }: any) => {
          console.log("onChange", onChange);
          console.log("value", value);

          return (
              <TextField
                {...register("nameField")}
                error={true}
                id="outlined-helperText"
                label={label}
                placeholder={defaultValue}
                value={value}
                onChange={onChange}
              />
          );
        }}
      />
    </Box>
  );
}

export default TextInput;
