import { useQuery } from "react-query";
import { getCountries } from "../api";

type Hook = () => string[];

const useContries: Hook = () => {
  const { data, isFetching } = useQuery<any>("photos", getCountries, {
    refetchOnWindowFocus: false,
    staleTime: 60000,
  });

  if (isFetching) return { isLoading: isFetching };

  return data.map((country: any) => country.name.common).sort();
};

export default useContries;
