import { useQuery } from "react-query";
import { getAircrafts } from "../api";

type HookParams = {
  offset: number;
  limit: number;
};

type Hook = (params: HookParams) => any;

const useAircrafts: Hook = (params) => {
  const { data, isFetching } = useQuery<any>("photos", () => getAircrafts(params), {
    refetchOnWindowFocus: false,
    staleTime: 60000,
  });

  if (isFetching) return { isLoading: isFetching, data: [] };

  return { data };
};

export default useAircrafts;
