import React, { useState } from 'react';
import Button from "@mui/material/Button";
import { TextInput, DropdownSelection, DateSelection, AlertMessage } from "../../../components";
import './ClientForm.css';
import { useClientStore } from '../../../store';
import { useCountries } from '../../../hooks';

interface Client {
  name: string;
  surname: string;
  country: string;
  birthday: string | null;
};


const ClientForm = () => {
  const [clients, setClients] = useState<Client[]>([]);
  const countries = useCountries();

  const {
    name,
    setName,
    surname,
    setSurname,
    country,
    setCountry,
    birthday,
    setBirthday,
  } = useClientStore((state) => ({
    name: state.name,
    setName: state.setName,
    surname: state.surname,
    setSurname: state.setSurname,
    country: state.country,
    setCountry: state.setCountry,
    birthday: state.birthday,
    setBirthday: state.setBirthday
  }));

  const handleOnSave = () => {
    setClients([
      ...clients,
      {
        name,
        surname,
        country,
        birthday,
      },
    ]);
  };

      return (
        <div className="client-form">
          <TextInput
            label={"Name"}
            defaultValue={"Name Here"}
            value={name}
            setValue={setName}
          />
          <TextInput
            label={"Surname"}
            defaultValue={"Name Here"}
            value={surname}
            setValue={setSurname}
          />
        {countries.length > 0 && <DropdownSelection
            label={"Countries"}
            value={country}
            setValue={setCountry}
            data={countries}
          />}
          <DateSelection label={"Birthday"} value={birthday} setValue={setBirthday}/>
          <div className="client-form--submition">
            <Button variant="outlined" onClick={handleOnSave}>Save</Button>
          </div>
          <div className="client-form--alert">
            <AlertMessage message={"teste"} />
          </div>
        </div>
      );
    };

export default ClientForm;
