// @ts-nocheck
/* import i18n from "i18next";
import { initReactI18next } from "react-i18next";
import Backend from 'i18next-http-backend';
import LanguageDetector from 'i18next-browser-languagedetector';
import globalEN from "./locales/en/global";

const resources = {
  'en-US': {
    'global': globalEN
  }
};

i18n.use(Backend).use(LanguageDetector).use(initReactI18next).init({
    fallbackLng: 'en', debug: true, detection: {
    order:['queryString', 'cookie'],
    cache: ['cookie'],
    resources,
  },
    interpolation: {
    escapeValue: false
  }
}) 



export default i18n;*/

import { initReactI18next } from "react-i18next";
import i18n from "i18next";
import Backend from "i18next-http-backend";

export const i18nBaseConfig = {
  fallbackLng: "pt-PT",
  whitelist: ["en-US", "fr-CA", "pt-PT"],
  defaultNS: "global",
  ns: ["global", "clients"],

  interpolation: {
    escapeValue: false,
  },
  react: {
    useSuspense: false,
  },
};

const i18nConfig = {
  ...i18nBaseConfig,
  lng: "pt-PT",
  backend: {
    crossDomain: true,
    loadPath: `http://localhost:3000/locales/{{lng}}/{{ns}}.json`,
  },
};

i18n.use(initReactI18next).use(Backend).init(i18nConfig);



export default i18n;
