import { useQuery } from "react-query";
import { getCountries } from "../api";

type Hook = () => {countries: string[], isLoading: boolean};

const useContries: Hook = () => {
  const { data, isFetching } = useQuery<any>("photos", getCountries, {
    refetchOnWindowFocus: false,
    staleTime: 60000,
  });

  if (isFetching) return { isLoading: isFetching, countries: [] };

  return {countries: data.map((country: any) => country.name.common).sort(), isLoading: isFetching};
};

export default useContries;
