import React, { useState } from 'react';
import { Button, ButtonGroup } from "@mui/material";
import { TextInput, DropdownSelection, DateSelection, AlertMessage } from "../../../components";
import './ClientForm.css';
import { useClientStore } from '../../../store';
import { useCountries } from '../../../hooks';
import { Client } from '../';
import { format } from "date-fns";
import { useForm, FormProvider } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { FormControl } from '@mui/material';
import { useTranslation } from "react-i18next";

const schema = yupResolver(yup
  .object({
    Name: yup.string().required().trim(),
    Surname: yup.string().required().trim(),
    country: yup.string().required().trim(),
    birthday: yup.date().required(),
  })
  .required());

interface InitialState {
  Name: string;
  Surname: string;
  country: string;
  birthday: string | null;
}

const getInitialState = (): InitialState => ({
  Name: '',
  Surname: '',
  country: '',
  birthday: null,
});

type Props = {
  clients: Client[];
  setClients: (value: Client[]) => void;
};


const ClientForm: React.FC<Props> = ({ clients, setClients }) => {
  const { t, i18n } = useTranslation();
  const [alert, setAlert] = useState<string>();
  const countries = useCountries();
  const defaultValues = getInitialState();

  const methods = useForm({
    mode: "onChange",
    reValidateMode: "onChange",
    criteriaMode: "all",
    resolver: schema,
    defaultValues,
  });

  const { watch, formState:{ errors }, setError } = methods;
  const formData = watch();

  const {
    name,
    setName,
    setSurname,
    country,
    setCountry,
    setBirthday,
  } = useClientStore((state) => ({
    name: state.name,
    setName: state.setName,
    surname: state.surname,
    setSurname: state.setSurname,
    country: state.country,
    setCountry: state.setCountry,
    birthday: state.birthday,
    setBirthday: state.setBirthday
  }));

  const handleOnSave = () => {

     if (formData.country === ""){
       setError(
         "country",
         { type: "focus", message: t("clients:error") },
         { shouldFocus: true }
       );
       return;
    }



     if (formData.birthday === "" || formData.birthday === null) {
       setError(
         "birthday",
         { type: "focus", message: t("clients:error") },
         { shouldFocus: true }
       );
      return;
      }

      if (Object.keys(errors).length > 0) return;

    const newClient = {
      name: formData.Name,
      surname: formData.Surname,
      country: formData.country,
      birthday: formData.birthday,
    };

    const birthdayDate = new Date(newClient.birthday);
    const yearsOld = parseInt(format(new Date(), "yyyy")) - parseInt(format(birthdayDate, "yyyy"));
    const birthdayDay = format(birthdayDate, "dd");
    const birthdayMonth = format(birthdayDate, "MM");

    const newAlert = t(
      "clients:form.alert",
      { name, country, birthdayDay, birthdayMonth, yearsOld }
    );

    setAlert(newAlert);
    setClients([...clients, newClient]);
    setName(newClient.name);
    setSurname(newClient.surname);
    setCountry(newClient.country);
    setBirthday(newClient.birthday);
  };

  const handleLanguageChange = (language: string) => {
    if(language === 'PT' && i18n.language === 'en-EN')
      i18n.changeLanguage('pt-PT');

    if (language === "EN" && i18n.language === "pt-PT")
      i18n.changeLanguage("en-US");
  };


      return (
        <FormProvider {...methods}>
          <div className="client-form">
            <div className="client-form--language">
              <ButtonGroup
                variant="contained"
                aria-label="outlined primary button group"
              >
                <Button>EN</Button>
                <Button>PT</Button>
              </ButtonGroup>
            </div>
            <FormControl>
              <TextInput
                label={"Name"}
                defaultValue={"Name Here"}
                controllerName={"Name"}
              />
              {
                <TextInput
                  label={"Surname"}
                  defaultValue={"Name Here"}
                  controllerName={"Surname"}
                />
              }
              {countries.length > 0 && (
                <DropdownSelection label={"Countries"} data={countries} />
              )}
              <DateSelection label={"Birthday"} />
              <div className="client-form--submition">
                <Button variant="outlined" onClick={handleOnSave}>
                  {t("global:action.save")}
                </Button>
              </div>
              <div className="client-form--alert">
                <AlertMessage message={alert} />
              </div>
            </FormControl>
          </div>
        </FormProvider>
      );
    };

export default ClientForm;
