import React from 'react';
import { SelectChangeEvent } from "@mui/material/Select";
import Button from "@mui/material/Button";
import { TextInput, DropdownSelection, DateSelection, AlertMessage } from "../../../components";
import './ClientForm.css';
import { useClientStore } from '../../../store';


const ClientForm = () => {
  const {
    name,
    setName,
    surname,
    setSurname,
    country,
    setCountry,
    birthday,
    setBirthday,
  } = useClientStore((state) => ({
    name: state.name,
    setName: state.setName,
    surname: state.surname,
    setSurname: state.setSurname,
    country: state.country,
    setCountry: state.setCountry,
    birthday: state.birthday,
    setBirthday: state.setBirthday
  }));

      const [age, setAge] = React.useState("");

      const handleChange = (event: SelectChangeEvent) => {
        setAge(event.target.value as string);
      };

      return (
        <div className="client-form">
          <TextInput label={"Name"} defaultValue={"Name Here"} />
          <TextInput label={"Surname"} defaultValue={"Name Here"} />
          <DropdownSelection label={"Countries"} />
          <DateSelection label={"Birthday"} />
          <div className="client-form--submition">
            <Button variant="outlined">Save</Button>
          </div>
          <div className="client-form--alert">
            <AlertMessage message={"teste"} />
          </div>
        </div>
      );
    };

export default ClientForm;
