import axios from "axios";
import { GetPhotosResponse } from "./types";

const url = (args: string[]) => ({
  getPhotos: `https://www.flickr.com/services/rest/?method=flickr.photos.search&api_key=${process.env.REACT_APP_FLICKR_API_KEY}&text=${args[0]}&format=json&nojsoncallback=1`,
});

export const getPhotos = async (text: string): Promise<GetPhotosResponse> => {
    const { data } = await axios.get(url([text]).getPhotos);

    return data;
};
