import * as React from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import './TextInput.css'
import { ChangeEvent } from "react";

type Event = ChangeEvent<HTMLInputElement | HTMLTextAreaElement>;

type Props = {
  label: string;
  defaultValue: string;
  value?: string;
  setValue: (newValue?: string) => void;
};

const TextInput: React.FC<Props> = ({label, defaultValue, value, setValue}) => {

  const handleOnChange = (e: Event) => setValue(e.target.value);


  return (
    <Box
      component="form"
      sx={{
        "& .MuiTextField-root": {
          marginTop: "8px",
          marginBottom: "8px",
          marginLeft: "4px",
          marginRight: "4px",
          display: "grid",
        },
      }}
      noValidate
      autoComplete="off"
    >
      <div>
        <TextField
          id="outlined-helperText"
          label={label}
          placeholder={defaultValue}
          value={value}
          onChange={handleOnChange}
        />
      </div>
    </Box>
  );
}

export default TextInput;
