import React from 'react';
import { styled } from "@mui/material/styles";
import Paper from "@mui/material/Paper";
import "./RotationItem.css";
import useRotationStack from '../../../../store/useRotationStack';


const Item = styled(Paper)(({ theme }) => ({
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: "center",
  color: theme.palette.text.secondary,
  width: "50vh",
  height: "16vh",
}));


type Props = {
  flight: any;
};

const RotationItem: React.FC<Props> = ({ flight }) => {
  const {
    id,
    origin,
    destination,
    readable_arrival,
    readable_departure,
  } = flight;
  const { addFlightToRotation } = useRotationStack((state) => ({
    addFlightToRotation: state.addFlightToRotation,
  }));

  const handleOnClick = () => addFlightToRotation(flight);

  return (
    <Item onClick={handleOnClick}>
      <div>
        <b>{id}</b>
      </div>
      <div className="card-body">
        <div className="card-body_column">
          <div>{origin}</div>
          <div>{readable_departure}</div>
        </div>
        <div className="card-body_column">
          <div>{destination}</div>
          <div>{readable_arrival}</div>
        </div>
      </div>
    </Item>
  );
};

export default RotationItem;
