import * as React from "react";
import TextField from "@mui/material/TextField";
import AdapterDateFns from "@mui/lab/AdapterDateFns";
import LocalizationProvider from "@mui/lab/LocalizationProvider";
import DatePicker from "@mui/lab/DatePicker";
import { Controller, useFormContext } from "react-hook-form";
import './DateSelection.css';
import { FormHelperText } from "@mui/material";

const CssTextField = styled(TextField)({'.css-9ddj71-MuiInputBase-root-MuiOutlinedInput-root.Mui-error .MuiOutlinedInput-notchedOutline':    { borderColor: '#d32f2f'}});

type Props = {
  label: string;
};

const DateSelection: React.FC<Props> = ({ label }) => {
  const { control } = useFormContext<any>();

  return (
    <div className="date-selection">
      <Controller
        control={control}
        name="birthday"
        render={({ field: { onChange, value }, fieldState: { error } }) => {
          const isError = Boolean(error);

          return (
            <LocalizationProvider dateAdapter={AdapterDateFns}>
              <DatePicker
                label={label}
                value={value}
                onChange={onChange}
                clearText={false}
                inputFormat={"dd/MM/yyyy"}
                renderInput={(params) => <TextField {...params} />}
              />
              {isError && (
                <FormHelperText error={isError}>Error</FormHelperText>
              )}
            </LocalizationProvider>
          );
        }}
      />
    </div>
  );
}

export default DateSelection;
